type RemotePointConfig struct {
	Code    int64  `json:"code" yaml:"code"`
	Message string `json:"message" yaml:"message"`
	Data    struct {
		Reverse  RemotePointInfo `json:"reverse" yaml:"reverse"`   // 右洞 下行
		Straight RemotePointInfo `json:"straight" yaml:"straight"` // 左洞 上行
	} `json:"data" yaml:"data"`
}

// RemotePointInfo 摄像头的关联关系
type RemotePointInfo struct {
	Nodes []RemotePoint `json:"nodes" yaml:"nodes"`
	Edges []RemoteEdge  `json:"edges" yaml:"edges"`
}

// RemotePoint 摄像头信息
type RemotePoint struct {
	Id          *int   `json:"id" yaml:"id"`                   // 卡口唯一标识 主建id
	ThirdCode   string `json:"thirdCode" yaml:"thirdCode"`     // 第三方映射编码 需要的卡口id
	BayonetType *int   `json:"bayonetType" yaml:"bayonetType"` // 位置类型: 0-主线; 1-支线
	AcType      *int   `json:"acType" yaml:"acType"`           // 卡口类型: 0-普通卡口; 1-入口; 2-出口
	PileNo      *int64 `json:"pileNo" yaml:"pileNo"`           // 桩号 100100
}

// RemoteEdge 摄像头关联关系的边
type RemoteEdge struct {
	From     *int `json:"from" yaml:"from"`         // 关联卡口起点 前一个卡口id
	To       *int `json:"to" yaml:"to"`             // 关联卡口终点 当前卡口id
	Distance *int `json:"distance" yaml:"distance"` // 距离卡口长度 单位m
}
